import{c as e}from"./app.a489c551.js";import{_ as c}from"./plugin-vue_export-helper.21dcd24c.js";const d={},o=e('<h1 id="send-\u548C-sync" tabindex="-1"><a class="header-anchor" href="#send-\u548C-sync" aria-hidden="true">#</a> Send \u548C Sync</h1><p><code>std::marker</code> \u6A21\u5757\u4E2D\uFF0C\u6709\u4E24\u4E2A trait\uFF1A<code>Send</code> \u548C <code>Sync</code>\uFF0C\u5B83\u4EEC\u4E0E\u591A\u7EBF\u7A0B\u5B89\u5168\u76F8\u5173\u3002</p><p>\u6807\u8BB0\u4E3A <code>marker trait</code> \u7684 trait\uFF0C\u5B83\u5B9E\u9645\u5C31\u662F\u4E00\u79CD\u7EA6\u5B9A\uFF0C\u6CA1\u6709\u65B9\u6CD5\u7684\u5B9A\u4E49\uFF0C\u4E5F\u6CA1\u6709\u5173\u8054\u5143\u7D20\uFF08associated items\uFF09\u3002\u4EC5\u4EC5\u662F\u4E00\u79CD\u7EA6\u5B9A\uFF0C\u5B9E\u73B0\u4E86\u5B83\u7684\u7C7B\u578B\u5FC5\u987B\u6EE1\u8DB3\u8FD9\u79CD\u7EA6\u5B9A\u3002\u4E00\u79CD\u7C7B\u578B\u662F\u5426\u52A0\u4E0A\u8FD9\u79CD\u7EA6\u5B9A\uFF0C\u8981\u4E48\u662F\u7F16\u8BD1\u5668\u7684\u884C\u4E3A\uFF0C\u8981\u4E48\u662F\u4EBA\u5DE5\u624B\u52A8\u7684\u884C\u4E3A\u3002</p><p><code>Send</code> \u548C <code>Sync</code> \u5728\u5927\u90E8\u5206\u60C5\u51B5\u4E0B\uFF08\u9488\u5BF9 Rust \u7684\u57FA\u7840\u7C7B\u578B\u548C std \u4E2D\u7684\u5927\u90E8\u5206\u7C7B\u578B\uFF09\uFF0C\u4F1A\u7531\u7F16\u8BD1\u5668\u81EA\u52A8\u63A8\u5BFC\u51FA\u6765\u3002\u5BF9\u4E8E\u4E0D\u80FD\u7531\u7F16\u8BD1\u5668\u81EA\u52A8\u63A8\u5BFC\u51FA\u6765\u7684\u7C7B\u578B\uFF0C\u8981\u4F7F\u5B83\u4EEC\u5177\u6709 <code>Send</code> \u6216 <code>Sync</code> \u7684\u7EA6\u5B9A\uFF0C\u53EF\u4EE5\u7531\u4EBA\u624B\u52A8\u5B9E\u73B0\u3002\u5B9E\u73B0\u7684\u65F6\u5019\uFF0C\u5FC5\u987B\u4F7F\u7528 <code>unsafe</code> \u524D\u7F00\uFF0C\u56E0\u4E3A Rust \u9ED8\u8BA4\u4E0D\u4FE1\u4EFB\u7A0B\u5E8F\u5458\uFF0C\u7531\u7A0B\u5E8F\u5458\u81EA\u5DF1\u63A7\u5236\u7684\u4E1C\u897F\uFF0C\u7EDF\u7EDF\u6807\u8BB0\u4E3A <code>unsafe</code>\uFF0C\u51FA\u4E86\u95EE\u9898\uFF08\u6BD4\u5982\uFF0C\u628A\u4E0D\u662F\u7EBF\u7A0B\u5B89\u5168\u7684\u5BF9\u8C61\u52A0\u4E0A <code>Sync</code> \u7EA6\u5B9A\uFF09\u7531\u7A0B\u5E8F\u5458\u81EA\u884C\u8D1F\u8D23\u3002</p><p>\u5B83\u4EEC\u7684\u5B9A\u4E49\u5982\u4E0B\uFF1A</p><p>\u5982\u679C <code>T: Send</code>\uFF0C\u90A3\u4E48\u5C06 <code>T</code> \u4F20\u5230\u53E6\u4E00\u4E2A\u7EBF\u7A0B\u4E2D\u65F6\uFF08\u6309\u503C\u4F20\u9001\uFF09\uFF0C\u4E0D\u4F1A\u5BFC\u81F4\u6570\u636E\u7ADE\u4E89\u6216\u5176\u5B83\u4E0D\u5B89\u5168\u60C5\u51B5\u3002</p><ol><li><code>Send</code> \u662F\u5BF9\u8C61\u53EF\u4EE5\u5B89\u5168\u53D1\u9001\u5230\u53E6\u4E00\u4E2A\u6267\u884C\u4F53\u4E2D\uFF1B</li><li><code>Send</code> \u4F7F\u88AB\u53D1\u9001\u5BF9\u8C61\u53EF\u4EE5\u548C\u4EA7\u751F\u5B83\u7684\u7EBF\u7A0B\u89E3\u8026\uFF0C\u9632\u6B62\u539F\u7EBF\u7A0B\u5C06\u6B64\u8D44\u6E90\u91CA\u653E\u540E\uFF0C\u5728\u76EE\u6807\u7EBF\u7A0B\u4E2D\u4F7F\u7528\u51FA\u9519\uFF08use after free\uFF09\u3002</li></ol><p>\u5982\u679C <code>T: Sync</code>\uFF0C\u90A3\u4E48\u5C06 <code>&amp;T</code> \u4F20\u5230\u53E6\u4E00\u4E2A\u7EBF\u7A0B\u4E2D\u65F6\uFF0C\u4E0D\u4F1A\u5BFC\u81F4\u6570\u636E\u7ADE\u4E89\u6216\u5176\u5B83\u4E0D\u5B89\u5168\u60C5\u51B5\u3002</p><ol><li><code>Sync</code> \u662F\u53EF\u4EE5\u88AB\u540C\u65F6\u591A\u4E2A\u6267\u884C\u4F53\u8BBF\u95EE\u800C\u4E0D\u51FA\u9519\uFF1B</li><li><code>Sync</code> \u9632\u6B62\u7684\u662F\u7ADE\u4E89\uFF1B</li></ol><p>\u63A8\u8BBA\uFF1A</p><ol><li><code>T: Sync</code> \u610F\u5473\u7740 <code>&amp;T: Send</code>\uFF1B</li><li><code>Sync + Copy = Send</code>\uFF1B</li><li>\u5F53 <code>T: Send</code> \u65F6\uFF0C\u53EF\u63A8\u5BFC\u51FA <code>&amp;mut T: Send</code>\uFF1B</li><li>\u5F53 <code>T: Sync</code> \u65F6\uFF0C\u53EF\u63A8\u5BFC\u51FA <code>&amp;mut T: Sync</code>\uFF1B</li><li>\u5F53 <code>&amp;mut T: Send</code> \u65F6\uFF0C\u4E0D\u80FD\u63A8\u5BFC\u51FA <code>T: Send</code>\uFF1B</li></ol><p>\uFF08\u6CE8\uFF1A<code>T</code>, <code>&amp;T</code>, <code>&amp;mut T</code>\uFF0C<code>Box&lt;T&gt;</code> \u7B49\u90FD\u662F\u4E0D\u540C\u7684\u7C7B\u578B\uFF09</p><p>\u5177\u4F53\u7684\u7C7B\u578B\uFF1A</p><ol><li>\u539F\u59CB\u7C7B\u578B\uFF08\u6BD4\u5982\uFF1A u8, f64\uFF09\uFF0C\u90FD\u662F <code>Sync</code>\uFF0C\u90FD\u662F <code>Copy</code>\uFF0C\u56E0\u6B64\u90FD\u662F <code>Send</code>\uFF1B</li><li>\u53EA\u5305\u542B\u539F\u59CB\u7C7B\u578B\u7684\u590D\u5408\u7C7B\u578B\uFF0C\u90FD\u662F <code>Sync</code>\uFF0C\u90FD\u662F <code>Copy</code>\uFF0C\u56E0\u6B64\u90FD\u662F <code>Send</code>\uFF1B</li><li>\u5F53 <code>T: Sync</code>\uFF0C<code>Box&lt;T&gt;</code>, <code>Vec&lt;T&gt;</code> \u7B49\u96C6\u5408\u7C7B\u578B\u662F <code>Sync</code>\uFF1B</li><li>\u5177\u6709\u5185\u90E8\u53EF\u53D8\u6027\u7684\u7684\u6307\u9488\uFF0C\u4E0D\u662F <code>Sync</code> \u7684\uFF0C\u6BD4\u5982 <code>Cell</code>, <code>RefCell</code>, <code>UnsafeCell</code>\uFF1B</li><li><code>Rc</code> \u4E0D\u662F <code>Sync</code>\u3002\u56E0\u4E3A\u53EA\u8981\u4E00\u505A <code>&amp;Rc&lt;T&gt;</code> \u64CD\u4F5C\uFF0C\u5C31\u4F1A\u514B\u9686\u4E00\u4E2A\u65B0\u5F15\u7528\uFF0C\u5B83\u4F1A\u4EE5\u975E\u539F\u5B50\u6027\u7684\u65B9\u5F0F\u4FEE\u6539\u5F15\u7528\u8BA1\u6570\uFF0C\u6240\u4EE5\u662F\u4E0D\u5B89\u5168\u7684\uFF1B</li><li>\u88AB <code>Mutex</code> \u548C <code>RWLock</code> \u9501\u4F4F\u7684\u7C7B\u578B <code>T: Send</code>\uFF0C\u662F <code>Sync</code> \u7684\uFF1B</li><li>\u539F\u59CB\u6307\u9488\uFF08<code>*mut</code>, <code>*const</code>\uFF09\u65E2\u4E0D\u662F <code>Send</code> \u4E5F\u4E0D\u662F <code>Sync</code>\uFF1B</li></ol><p>Rust \u6B63\u662F\u901A\u8FC7\u8FD9\u4E24\u5927\u6B66\u5668\uFF1A<code>\u6240\u6709\u6743\u548C\u751F\u547D\u5468\u671F</code> + <code>Send \u548C Sync</code>\uFF08\u672C\u8D28\u4E0A\u4E3A\u7C7B\u578B\u7CFB\u7EDF\uFF09\u6765\u4E3A\u5E76\u53D1\u7F16\u7A0B\u63D0\u4F9B\u4E86\u5B89\u5168\u53EF\u9760\u7684\u57FA\u7840\u8BBE\u65BD\u3002\u4F7F\u5F97\u7A0B\u5E8F\u5458\u53EF\u4EE5\u653E\u5FC3\u5728\u5176\u4E0A\u6784\u5EFA\u7A33\u5065\u7684\u5E76\u53D1\u6A21\u578B\u3002\u8FD9\u4E5F\u6B63\u662F Rust \u7684\u6838\u5FC3\u8BBE\u8BA1\u89C2\u7684\u4F53\u73B0\uFF1A\u5185\u6838\u53EA\u63D0\u4F9B\u6700\u57FA\u7840\u7684\u539F\u8BED\uFF0C\u771F\u6B63\u7684\u5B9E\u73B0\u80FD\u5206\u79BB\u51FA\u53BB\u5C31\u5206\u79BB\u51FA\u53BB\u3002\u5E76\u53D1\u4E5F\u662F\u5982\u6B64\u3002</p>',15);function n(l,i){return o}var a=c(d,[["render",n],["__file","sendsync.html.vue"]]);export{a as default};
